(()=>{const e=document.querySelector(".content__btn");var t=document.querySelector(".module__overlay"),a=document.querySelector(".alert"),l=document.querySelector(".alert__close");const o=[t,a],s=[l,t];const c=new class{value=0;container=document.querySelector(".alert__count");resetButton=document.querySelector(".alert__btn");localStorageActualValue=localStorage.getItem("taskStorage");init(){if(this.resetButton.addEventListener("click",()=>this.resetValue()),!this.localStorageActualValue)return this.setLocalStorageValue("0");this.value=localStorage.getItem("taskStorage")}click(){4<=this.value&&this.displayResetButton(),this.value++,this.container.innerText=this.value,this.setLocalStorageValue(this.value.toString())}displayResetButton(){this.resetButton.style.display="inline-block"}resetValue(){this.value=0,this.container.innerText=this.value,this.resetButton.style.display="none"}setLocalStorageValue(e){localStorage.setItem("taskStorage",e)}};c.init(),e.addEventListener("click",()=>{c.click(),o.forEach(e=>e.classList.add("active"))}),s.forEach(e=>e.addEventListener("click",()=>o.forEach(e=>e.classList.remove("active"))))})();