(()=>{const t=document.querySelector(".content__btn");var e=document.querySelector(".module__overlay"),a=document.querySelector(".alert"),s=document.querySelector(".alert__close");const c=[e,a],l=[s,e];const o=new class{value=0;container=document.querySelector(".alert__count");resetButton=document.querySelector(".alert__btn");localStorageActualValue=localStorage.getItem("taskStorage");init(){if(this.resetButton.addEventListener("click",()=>this.resetValue()),!this.localStorageActualValue)return this.setLocalStorageValue("0");this.value=localStorage.getItem("taskStorage")}click(){4<=this.value&&!this.resetButton.classList.contains("active")&&this.displayResetButton(),this.value++,this.container.innerText=this.value,this.setLocalStorageValue(this.value.toString())}displayResetButton(){this.resetButton.classList.add("active")}resetValue(){this.value=0,this.container.innerText=this.value,this.resetButton.classList.remove("active")}setLocalStorageValue(t){localStorage.setItem("taskStorage",t)}};o.init(),t.addEventListener("click",()=>{o.click(),c.forEach(t=>t.classList.add("active"))}),l.forEach(t=>t.addEventListener("click",()=>c.forEach(t=>t.classList.remove("active"))))})();