(()=>{const e=document.querySelector(".content__btn");var c=document.querySelector(".module__overlay"),t=document.querySelector(".alert"),o=document.querySelector(".alert__close");const r=[c,t],n=[o,c];const a=new class{value=0;container=document.querySelector(".alert__count");click(){this.value++,this.container.innerText=this.value}};e.addEventListener("click",()=>{a.click(),r.forEach(e=>e.classList.add("active"))}),n.forEach(e=>e.addEventListener("click",()=>r.forEach(e=>e.classList.remove("active"))))})();