(()=>{const e=document.querySelector(".content__btn");var t=document.querySelector(".module__overlay"),n=document.querySelector(".alert"),s=document.querySelector(".alert__close");const c=[t,n],i=[s,t];const l=new class{value=0;container=document.querySelector(".alert__count");resetButton=document.querySelector(".alert__btn");init(){this.resetButton.addEventListener("click",()=>this.resetValue())}click(){4<=this.value&&this.displayResetButton(),this.value++,this.container.innerText=this.value}displayResetButton(){this.resetButton.style.display="inline-block"}resetValue(){this.value=0,this.container.innerText=this.value,this.resetButton.style.display="none"}};l.init(),e.addEventListener("click",()=>{l.click(),c.forEach(e=>e.classList.add("active"))}),i.forEach(e=>e.addEventListener("click",()=>c.forEach(e=>e.classList.remove("active"))))})();