(()=>{const e=document.querySelector(".content__btn");var c=document.querySelector(".module__overlay"),t=document.querySelector(".alert"),o=document.querySelector(".alert__close");const r=[c,t],a=[o,c];e.addEventListener("click",()=>{r.forEach(e=>e.classList.add("active"))}),a.forEach(e=>e.addEventListener("click",()=>r.forEach(e=>e.classList.remove("active"))))})();